var icon=document.querySelector(".icon"),menu=document.querySelector(".menu ul");icon.addEventListener("click",()=>{icon.classList.toggle("active"),menu.classList.toggle("show")});let sections=document.querySelectorAll("section"),links=document.querySelectorAll(".navbar .container ul li a"),menu_links=document.querySelectorAll(".menu ul li a");function removeactive(e){e.forEach(e=>{e.classList.remove("active")})}function setactive(e){e.classList.add("active")}function animate(e,...t){window.addEventListener("scroll",()=>{window.scrollY>=e.offsetTop-70&&t.forEach(e=>{let t=Array.from(e);0===t.length?e.style.cssText="opacity:1;transform:translate(0,0);-webkit-transform:translate(0,0));-moz-transform:translate(0,0);-ms-transform:translate(0,0);-o-transform:translate(0,0);transform: scale(1);-webkit-transform: scale(1);-moz-transform: scale(1);-ms-transform: scale(1);-o-transform: scale(1);":t.forEach(e=>{e.style.cssText="opacity:1;transform:translate(0,0);-webkit-transform:translate(0,0));-moz-transform:translate(0,0);-ms-transform:translate(0,0);-o-transform:translate(0,0);transform: scale(1);-webkit-transform: scale(1);-moz-transform: scale(1);-ms-transform: scale(1);-o-transform: scale(1);"})})})}window.addEventListener("scroll",()=>{let e=document.documentElement.scrollTop;sections.forEach(t=>{e>=t.offsetTop-70&&e<=t.offsetHeight+t.offsetTop&&(links.forEach(e=>{t.id.toLocaleLowerCase()==e.className.toLocaleLowerCase()&&(console.log(e.textContent.toLocaleLowerCase()),removeactive(links),setactive(e))}),menu_links.forEach(e=>{t.id.toLocaleLowerCase()==e.className.toLocaleLowerCase()&&(removeactive(menu_links),setactive(e))}))})});